#include <stdio.h>
int main() {
  int arr[] = {1, 2, 3, 4, 5};
  int start = 0, end = 4;
  while(start < end) {
    int temp = arr[start];
    arr[start] = arr[end];
    arr[end] = temp;
    start++;
    end--;
  }
  for(int i = 0; i < 5; i++) {
    printf("%d ", arr[i]);
  }
  return 0;
}

#include <stdio.h>
int main() {
  int arr[] = {1, 2, 3, 4, 5};
  int max = arr[0];
  for(int i = 1; i < 5; i++) {
    if(arr[i] > max) {
      max = arr[i];
    }
  }
  printf("Max Element: %d", max);
  return 0;
}

